const months = [
  'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
  'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'
];

function getDaysInMonth(year, monthIndexZeroBased) {
  return new Date(year, monthIndexZeroBased + 1, 0).getDate();
}

// Signal handler â€” runs when DateUpdated is fired
Script.Signals.DateUpdated = function () {
  let hasUserInput = Script.Inputs.HasUserInput;
  let rawOffset = Script.Inputs.MonthOffset;
  let monthOffset = (!isNaN(Number(rawOffset))) ? Number(rawOffset) : 0;
  let yearOffset = 0;

  if (hasUserInput && monthOffset <= 0) {
    yearOffset -= 1;
  }

  let today = new Date();
  let month = today.getMonth() + monthOffset;
  let year = today.getFullYear() + yearOffset;

  while (month > 11) {
    month -= 12;
    year += 1;
  }
  while (month < 0) {
    month += 12;
    year -= 1;
  }

  console.log("Final MonthOffset used:", monthOffset);
  console.log("Days in month:", getDaysInMonth(year, month));

  console.log("Raw MonthOffset input:", Script.Inputs.MonthOffset);
  console.log("Type of MonthOffset input:", typeof Script.Inputs.MonthOffset);

  console.log("Number of days in " + months[month] + " " + year + " is " + getDaysInMonth(year, month));

  console.log("MonthOffset input:", Script.Inputs.MonthOffset);


  Script.Outputs.CalendarMonth = months[month];
  Script.Outputs.CalendarYear = year;
}

