let inputDateString = Inputs.dateString

if (!inputDateString) {
  return
}


let [day, month, year] = inputDateString.split('-').map(Number);
const dateObj = new Date(year, month - 1, day);

function isValidDateString(dateString) {
  // First, check basic format
  const datePattern = /^\d{2}-\d{2}-\d{4}$/;
  if (!datePattern.test(dateString)) {
    return false;
  }

  // Check if date components match exactly
  return (
    dateObj.getFullYear() === year &&
    dateObj.getMonth() === month - 1 &&
    dateObj.getDate() === day
  );
}

let validated = isValidDateString(inputDateString)

Outputs.showError = !validated

if (validated && Inputs.send) {
  Outputs.date = dateObj
  Outputs.done();
}
