parameters.functionScript:
let key = Inputs.key
let value = Inputs.value
let action = Inputs.action;

if (!key) {
    Outputs.failure()
    return
}

if (action === "Save") {
    localStorage.setItem(key, JSON.stringify(value));
    Outputs.success();
}else if(action === "Load"){
    Outputs.data = JSON.parse(localStorage.getItem(key))
    Outputs.success();
}

Outputs.Data = {
    labels: [
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July"
    ],
    datasets: [
        {
            label: "Fully Rounded",
            data: [99, 41, 94, 1, 32, -63, 36],
            borderColor: "rgb(255, 99, 132)",
            backgroundColor: "rgba(255, 99, 132, 0.5)",
            borderWidth: 2,
            borderRadius: 1.8,
            borderSkipped: false
        },
        {
            label: "Small Radius",
            data: [-59, -80, -62, -25, -40, 58, 95],
            borderColor: "rgb(54, 162, 235)",
            backgroundColor: "rgba(54, 162, 235, 0.5)",
            borderWidth: 2,
            borderRadius: 5,
            borderSkipped: false
        }
    ]
}

Outputs.Scales = {
    x: {
        stacked: true
    },
    y: {
        stacked: true
    }
}

let key = String(Inputs.key)
if (!key) {
    Outputs.failure()
    return
}

if (Inputs.state === "Save") {
    localStorage.setItem(key, JSON.stringify(Inputs.value))
    Outputs.saved();
} else {
    let data = localStorage.getItem(key)
    if (!data) {
        Outputs.failure()
        return
    }
    Outputs.data = JSON.parse(data)
    Outputs.loaded();
}

// Make sure you can click through parent div
// so it doesn't block like normal popups
Inputs.This.getDOMElement().style.pointerEvents = 'none'
Inputs.This.getDOMElement().parentElement.style.pointerEvents = 'none'
